import axios from 'axios'

const BACK_API_URL = 'http://localhost:8080'

class EmployeeDataService {

    retrieveAllEmployees() {
        //console.log('executed service')
        return axios.get(`${BACK_API_URL}/empdir`);
    }

    retrieveEmployee(id) {
        //console.log('executed service')
        return axios.get(`${BACK_API_URL}/empdir/${id}`);
    }

    deleteEmployee(id) {
        //console.log('executed service')
        return axios.delete(`${BACK_API_URL}/empdir/${id}`);
    }

    updateEmployee(id, employee) {
        //console.log('executed service')
        return axios.put(`${BACK_API_URL}/empdir/${id}`, employee);
    }

    createEmployee(employee) {
        //console.log('executed service')
        return axios.post(`${BACK_API_URL}/empdir/`, employee);
    }
}

export default new EmployeeDataService()
